import React, { Component } from 'react';
import { Card, Tag, Input, Checkbox, Select, message, Table } from 'antd';
import 'antd/dist/antd.css';

const { Search } = Input;

const { Option } = Select;

export class App extends Component {
  state = {
    denomination: false,
    denominationAmount: 0,
    totalAmount: 100000,
    dispatchAmount: 0
  }

  onChange = (e) => {
    this.setState({denomination: e.target.checked, denominationAmount: 0});
  }

  handleChange = (value) => {
    this.setState({denominationAmount: parseInt(value)});
  }

  onDispatch = (value) => {
    if(this.state.dispatchAmount === 0 || parseInt(value) === 0) {
      message.warning('Please input a amount');
      return;
    } else if (this.state.dispatchAmount < 0) {
      message.error('Please input a valid amount');
    }
  }

  render() {
    const {denomination, totalAmount} = this.state;
    return (
      <Card title="ATM Machine" type="inner" style={{ width: 300, textAlign: "center", margin: "20px auto" }}>

        <h4>Total Amount: {totalAmount}</h4>

        <p>Available Notes :</p>
        <Tag color="magenta">10</Tag>
        <Tag color="red">20</Tag>
        <Tag color="cyan">50</Tag>
        <br />
        <Tag color="blue">100</Tag>
        <Tag color="green">200</Tag>
        <Tag color="lime">500</Tag>
        <Tag color="purple">1000</Tag>

        <Checkbox onChange={this.onChange} style={{margin: "50px 0"}}>Denomination</Checkbox>
        <Select style={{ width: 80 }} onChange={this.handleChange} size="small" disabled={!denomination}>
          <Option value="10">10</Option>
          <Option value="20">20</Option>
          <Option value="50">50</Option>
          <Option value="100">100</Option>
          <Option value="200">200</Option>
          <Option value="500">500</Option>
          <Option value="1000">1000</Option>
        </Select>

        <Search placeholder="Amount" allowClear enterButton="Dispatch" size="small" onSearch={this.onDispatch} onChange={(e) => {this.setState({dispatchAmount: e.target.value})}} />

        <Table dataSource={data}>
    <ColumnGroup title="Name">
      <Column title="First Name" dataIndex="firstName" key="firstName" />
      <Column title="Last Name" dataIndex="lastName" key="lastName" />
    </ColumnGroup>
    <Column title="Age" dataIndex="age" key="age" />
    <Column title="Address" dataIndex="address" key="address" />
    <Column
      title="Tags"
      dataIndex="tags"
      key="tags"
      render={tags => (
        <>
          {tags.map(tag => (
            <Tag color="blue" key={tag}>
              {tag}
            </Tag>
          ))}
        </>
      )}
    />
    <Column
      title="Action"
      key="action"
      render={(text, record) => (
        <Space size="middle">
          <a>Invite {record.lastName}</a>
          <a>Delete</a>
        </Space>
      )}
    />
  </Table>
      </Card>
    )
  }
}

export default App
