import React, { Component } from 'react';
import { Card, Tag, Input, Checkbox, Select, message } from 'antd';
import 'antd/dist/antd.css';

const { Search } = Input;

const { Option } = Select;

export class App extends Component {
  state = {
    denomination: false,
    denominationAmount: 0,
    totalAmount: 100000,
    dispatchAmount: 0
  }

  onChange = (e) => {
    this.setState({denomination: e.target.checked, denominationAmount: 0});
  }

  handleChange = (value) => {
    this.setState({denominationAmount: parseInt(value)});
